int i=4; assertEquals(16,Class.nmaln(i));
}

}

exercise.testClass:
	public class Class{
	public static void main(String[] args){
	System.out.println(nmaln(2));
	}
	public static int nmaln(int i){
	if(i==2)
		return 4 ;
	if(i==3)
		return 9;
	if(i==4)
		return 16;

		return 0;
	}
}

